package whil.e;

public class Divices {             // divices of given numbers

	public static void main(String[] args) {
		// TODO Auto-generated method stub
                int no=30;
               int div=1;
               while(no>=div)
               {
            	   if(no%div==0)
            	   {
            		   System.out.println(div);
            	   }
            	   div=div+1;
               }
            		
	}

}
