package whil.e;

public class Print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int no=1;
        int i=1;
        while(no<=16)
        {
        	System.out.print(no+" ");
        	no=no+i;
        	i=i+1;
        	
        }
	}

}
