package whil.e;

public class Squareroot20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             int no=36;
             int i=2;
             
             while (i<no/2)
             {
            	 if (no/i==i)
            	 {
            		 System.out.print(i+" "+"is the square root");
            		 break;
            	 }
            	 i=i+1;
            	 
             }
	}

}
